[message_excerpt_length]\": {\n        \"value\": \"31\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2024-08-27 09:54:07\"\n    },\n    \"classic-blogbell::theme_options[disable_recent_section]\": {\n        \"value\": \"false\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2024-08-27 09:54:27\"\n    },\n    \"show_on_front\": {\n        \"value\": \"posts\",\n        \"type\": \"option\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2024-08-27 09:56:27\"\n    },\n    \"page_on_front\": {\n        \"value\": \"0\",\n        \"type\": \"option\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2024-08-27 09:56:27\"\n    },\n    \"page_for_posts\": {\n        \"value\": \"0\",\n        \"type\": \"option\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2024-08-27 09:56:27\"\n    },\n    \"classic-blogbell::theme_options[disable_homepage_content_section]\": {\n        \"value\": \"true\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2024-08-27 09:56:27\"\n    }\n}', '', '', 'trash', 'closed', 'closed', '', '25538cbf-f1b8-4089-9ea8-5200bfa171dc', '', '', '2024-08-27 11:56:39', '2024-08-27 09:56:39', '', 0, 'https://stypendium.zekiu.xyz/?p=18', 0, 'customize_changeset', '', 0),
